// Das Javascript muss im Rahmen der Aufgabenserie nicht modifiziert werden

function getDeviceStatus(id) {
  const deviceEl = document.querySelector(`[data-device-id="${id}"]`);
  return deviceEl ? deviceEl.dataset.status : 'stopped';
}

function setDeviceStatus(id, newStatus) {
  const deviceEl = document.querySelector(`[data-device-id="${id}"]`);
  if (deviceEl) {
    deviceEl.dataset.status = newStatus;
    updateUI();
  }
}

function updateUI() {
  // Alle Geräte durchgehen und UI basierend auf data-status aktualisieren
  document.querySelectorAll('.device').forEach(deviceEl => {
    const status = deviceEl.dataset.status;
    const startStopBtn = deviceEl.querySelector('.toggle-btn:not(.pause-btn)');
    const pauseBtn = deviceEl.querySelector('.pause-btn');

    switch (status) {
      case 'running':
        startStopBtn.innerHTML = '<i class="fas fa-stop"></i>';
        startStopBtn.classList.add('stop');
        startStopBtn.classList.remove('start');
        pauseBtn.style.display = 'inline';
        break;
      case 'paused':
        startStopBtn.innerHTML = '<i class="fas fa-play"></i>';
        startStopBtn.classList.add('start');
        startStopBtn.classList.remove('stop');
        pauseBtn.style.display = 'none';
        break;
      case 'error':
        // Error devices bleiben wie sie sind
        pauseBtn.style.display = 'none';
        break;
      case 'stopped':
      default:
        startStopBtn.innerHTML = '<i class="fas fa-play"></i>';
        startStopBtn.classList.add('start');
        startStopBtn.classList.remove('stop');
        pauseBtn.style.display = 'none';
        break;
    }
  });

  // Aktive Geräte zählen (nur running status)
  const activeCount = document.querySelectorAll('[data-status="running"]').length;
  document.getElementById("active-count").textContent = activeCount;
}

function toggleSidebar() {
  htmlEl = document.getElementById('sidebar');
  if (htmlEl && htmlEl.style.left == '0px') {
    htmlEl.style.left = '-1000px';
  } else {
    htmlEl.style.left = '0px';
  }
}

function openPopup(id) {
  document.getElementById(id).style.display = 'flex';
}

function closePopup(id) {
  document.getElementById(id).style.display = 'none';
}

function startAllDevices() {
  document.querySelectorAll('.device').forEach(deviceEl => {
    const startStopBtn = deviceEl.querySelector('.toggle-btn:not(.pause-btn)');
    // Nur starten wenn nicht disabled oder error
    if (!startStopBtn.classList.contains('disabled') &&
      !startStopBtn.classList.contains('issue') &&
      deviceEl.dataset.status !== 'error') {
      deviceEl.dataset.status = 'running';
    }
  });
  updateUI();
}

function deactivateAllDevices() {
  document.querySelectorAll('.device').forEach(deviceEl => {
    // Error devices bleiben error, andere werden stopped
    if (deviceEl.dataset.status !== 'error') {
      deviceEl.dataset.status = 'stopped';
    }
  });
  updateUI();
}

function pauseAllDevices() {
  document.querySelectorAll('.device').forEach(deviceEl => {
    // Nur running devices pausieren
    if (deviceEl.dataset.status === 'running') {
      deviceEl.dataset.status = 'paused';
    }
  });
  updateUI();
}

// Event Listener für alle Geräte
document.querySelectorAll('.device').forEach(deviceEl => {
  const startStopBtn = deviceEl.querySelector('.toggle-btn:not(.pause-btn)');
  const pauseBtn = deviceEl.querySelector('.pause-btn');
  const id = deviceEl.dataset.deviceId;

  if (startStopBtn && !startStopBtn.classList.contains('disabled')) {
    startStopBtn.addEventListener('click', (e) => {
      // Error devices ignorieren
      if (deviceEl.dataset.status === 'error') {
        return;
      }

      // Status basierend auf aktuellen data-status wechseln
      const currentStatus = deviceEl.dataset.status;
      if (currentStatus === 'running') {
        deviceEl.dataset.status = 'stopped';
      } else {
        deviceEl.dataset.status = 'running';
      }
      updateUI();
    });
  }

  if (pauseBtn) {
    pauseBtn.addEventListener('click', () => {
      if (deviceEl.dataset.status === 'running') {
        deviceEl.dataset.status = 'paused';
        updateUI();
      }
    });
  }
});

// UI beim Laden initialisieren
updateUI();
